import React, { useEffect, useState } from 'react';
import { GameOfLife } from './components/Main';

type arrType = Array<boolean>;
const arrGenerator = (): arrType => {
  const arrItems = Math.floor((window.innerHeight / 25) * (window.innerWidth / 25));
  return Array.from({ length: arrItems }, () => false);
};
const randomDarkGreenColor = (): string => {
  const greenValue = Math.floor(Math.random() * 51);
  return `rgb(0, ${greenValue}, 0)`;
};
function App() {
  const [theme, setTheme] = useState(arrGenerator);
  const [dimensions, setDimensions] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () => {
      setDimensions({ width: window.innerWidth, height: window.innerHeight });
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);
  console.log(dimensions);
  return (
    <div className='App'>
      {theme?.map((item, ind) => (
        <div key={ind} style={{ backgroundColor: randomDarkGreenColor() }} className='theme'></div>
      ))}
      <GameOfLife />
    </div>
  );
}

export default App;
