import React, { useState, useEffect } from 'react';
import '../index.css';

const arrGenerator = () => {
  const numRows = 25;
  const numCols = 25;
  return Array.from({ length: numRows }, () => Array(numCols).fill(false));
};

export const GameOfLife = () => {
  const [grid, setGrid] = useState<Array<Array<boolean>>>(arrGenerator);
  const [run, setRun] = useState(false);

  const searchNeighbors = (arr, rowIndex, colIndex) => {};

  const nextGeneration = (arr) => {
    arr.map((row, rowIndex) =>
      row.map((col, colIndex) => {
        const res = searchNeighbors(arr, rowIndex, colIndex);
        if (col && (res === 2 || res === 3)) {
          arr[rowIndex][colIndex] = true;
        }
        if (!col && res === 3) {
          arr[rowIndex][colIndex] = true;
        }
        return;
      }),
    );
  };

  useEffect(() => {
    if (!run) return;
    const interval = setInterval(() => {
      setGrid((prev) => nextGeneration(prev));
    }, 1000);
    return () => clearInterval(interval);
  }, [run]);
  return (
    <div className='container'>
      <div className='grid'>
        {grid?.map((row, rowIndex) =>
          row?.map((item, colIndex) => (
            <div
              style={item ? { backgroundColor: 'black' } : { backgroundColor: 'white' }}
              onClick={() => toggleCell(rowIndex, colIndex)}
              className='cell'
            ></div>
          )),
        )}
      </div>
      <button onClick={() => setRun(!run)}>{run ? 'Stop' : 'Start'}</button>
    </div>
  );
};

